// function

const saludar = function( nombre ) {
    return `Hola ${ nombre }`
}

 // Arrow function

const saludar2 = ( nombre ) => {
    return `Hola, soy ${ nombre }`
} 

const saludar3 = ( nombre ) =>  `Hola, soy ${ nombre }`;

const saludar4 = ( ) =>  `Hola mundo`;

console.log(saludar4());
