// Operador condicional ternario

const activo = true;

let mensaje = '';

// if (activo) {
//     mensaje = 'activo';
// } else {
//     mensaje = 'inactivo';
// }

mensaje = activo ? 'activo': 'inactivo';

console.log(mensaje);
